mapper.select:
select no, member_id, content_id, content_festival_id, content_title
  from members_likes
 where member_id = #{memberId}

mapper.delete:
delete from members_likes
 where member_id = #{memberId}


		and content_id = #{contentId}


		and content_festival_id = #{contentFestivalId}

mapper.select:
select member_id, member_pw, member_name, join_date, member_email, age
  from members
 where member_id = #{memberId} and member_pw = #{memberPw}

select member_id, member_pw, member_name, join_date, member_email, age, member_board_count
  from members
 where member_id = #{memberId}

select member_id, member_pw, member_name, join_date, member_email, age, member_board_count
  from members
 where member_id = #{memberId}

mapper.update:
update members
member_pw = #{memberPw}, member_name = #{memberName}, member_email = #{memberEmail}, age = #{age},
where member_id = #{memberId}

update members
set token = #{token}
where member_id = #{memberId}

mapper.select:
select token
from members
where member_id = #{memberId}

mapper.update:
update members
set token = #{token, jdbcType=VARCHAR}
where member_id = #{memberId}
